<?php	echo "<div id='sidemenu'>";		include("_private/sidemenu_p.php");	echo "</div>";	if($data_id){		$q=get_text_data($data_id);		while($data=mysql_fetch_assoc($q))		{			$text_data_id=$data['id']; 				$heading=$data['heading']; 				$content=$data['content']; 			if($_GET['order']==1) include("_private/order.php");			echo "<div class='embossed'>";				echo "<h2>".$heading."</h2>";					display_multi_image_slides($page_id,$text_data_id);					echo "<p>".nl2br($content)."</p>";					display_multi_link($page_id,$text_data_id);					if($_GET['order']!=1) echo "<center><a href='index.php?page=".$page."&&data_id=".$data_id."&&order=1' class='bluebutton'>Order Now</a></center>";			echo "</div>";		}	}	else{		$qc=mysql_query("SELECT id, heading FROM text_data WHERE page_id='".$page_id."'  AND hidden='0' ORDER BY priority DESC");		$num=mysql_num_rows($qc);		if($num>0)		{				if($_GET['start'])			{				$start=mysql_escape_string($_GET['start']);			}			else			{				$start=0;			}			$perpage=12;			$pageurl="index.php?page=".$page;			$q=mysql_query("SELECT id, heading FROM text_data WHERE page_id='".$page_id."'  AND hidden='0' ORDER BY priority DESC LIMIT ".$start.",".$perpage."");			$pagination=get_pagination($perpage,$start,$pageurl,$qc);			echo "<h1>".$page_visual." ".$pagination."</h1>";			echo "<table class='decorfulwidth' align='center'>";				echo "<tr>";				$count=0;				while($data=mysql_fetch_assoc($q))				{					if(($count%3)==0)					{						echo "</tr><tr>";					}					$text_data_id=$data['id']; 						$heading=$data['heading']; 					echo "<td><a href='index.php?page=".$page."&&data_id=".$text_data_id."'>";											echo "<h3>".$heading."</h3>"; 						display_preview_image($page_id,$text_data_id);					echo "</a></td>";					$count++;				}				echo "</tr>";			echo "</table>";			echo $pagination;		}	}?>